mapper.select:
select a.mc_id, mb_id, writer, content, mod_at
from (
	select mc_id from m_comment
	where mb_id = #{mbId}
	limit #{pgvo.pageStart}, #{pgvo.qty}
)a inner join m_comment b
on a.mc_id = b.mc_id
order by a.mc_id

mapper.update:
update m_comment
set content = #{content}, mod_at = now()
where mc_id = #{mcId}
