mapper.select:
select mr.mid , mr.writer , mr.reg_at , mr.mod_at , mr.content , u.profile_img , u.font_color 
from m_review mr  left join `user` u on mr.writer = u.email 
where mid = #{mid}

select mr.mid , mr.writer , mr.reg_at , mr.mod_at , mr.content , u.profile_img , u.font_color 
from m_review mr  left join `user` u on mr.writer = u.email 
where mid = #{mid} and writer = #{writer}
